"use strict";function init(){toggleSoundOnClick(),changePage(),swiperInit()}function toggleSoundOnClick(){var e=document.querySelector('[data-btn="sound"]');e&&e.addEventListener("click",(function(){e.classList.toggle("off")}))}function changePage(){document.querySelectorAll('[data-btn="change-page"]').forEach((function(e){e.addEventListener("click",(function(){var e=document.querySelector('section[id^="page"].active'),t=parseInt(e.id.replace("page",""));e.classList.remove("active");var n=t%3+1;document.querySelector("#page".concat(n)).classList.add("active")}))}))}function swiperInit(){new Swiper(".swiper",{loop:!0,navigation:{nextEl:".swiper-button-next",prevEl:".swiper-button-prev"}})}document.addEventListener("DOMContentLoaded",(function(){init()}));